mapper.insert:
INSERT INTO
    RECIPE (
		recipe_type
		, recipe_name
		, recipe_explanation
		, recipe_file_name
		, recipe_tip
		, recipe_person
		, recipe_time
	)
VALUES
	(
		#{recipeType}
		, #{recipeName}
		, #{recipeExplanation}
		, #{recipeFileName}
		, #{recipeTip}
		, #{recipePerson}
		, #{recipeTime}
	)


	SELECT RECIPE_NO FROM RECIPE order by RECIPE_REG_DT desc limit 1

INSERT INTO RECIPE_ETC
	(
		RECIPE_ETC
	 	, RECIPE_ETC_INGREDIENT
		, RECIPE_ETC_QUANTITY
		, RECIPE_ETC_TYPE
		, RECIPE_NO
	)
VALUES
    (
		#{recipeEtc}
  		, #{recipeEtcIngredient}
		, #{recipeEtcQuantity}
		, #{recipeEtcType}
		, #{recipeNo}
	)


SELECT RECIPE_NO FROM RECIPE order by RECIPE_REG_DT desc limit 1

INSERT INTO RECIPE_DETAIL
(
	recipe_detail_step
	, recipe_detail_content
	, recipe_detail_image_name
	, RECIPE_NO
)
VALUE
(
	#{recipeDetailStep}
	, #{recipeDetailContent}
	, #{recipeFileName}
	, #{recipeNo}
)
